import $ from "jquery";
import axios from "axios";
import port from "./../../data/port.json";
import { useRef } from "react";

const SignUpForm = (props) => {
  const { signUpData, onChangeSignUpData, setSignUpData } = props;

  // DB와 요청/응답을 수행한다면 (무조건) 비동기 처리
  const onClickSignUpButton = async () => {
    if (signUpData.email === "") {
      alert("이메일을 입력해주세요");
      $("#email").focus();
      return; //onClickLoginButton 이벤트핸들러 자체를 나감
    }

    if (signUpData.password === "") {
      alert("비밀번호를 입력해주세요");
      $("#password").focus();
      return;
    }
    if (signUpData.rePassword === "") {
      alert("비밀번호를 입력해주세요");
      $("#rePassword").focus();
      return;
    }
    if (signUpData.name === "") {
      alert("비밀번호를 입력해주세요");
      $("#name").focus();
      return;
    }
    // 요청할 url과 보내고싶은 데이터
    return await axios.post(port.url + "/user/login", signUpData); //  user/login에 해당하는 node.js 서버에 요청. signInData를 응답하는 서버단의 참조용 데이터로 전송.
  };
  return (
    <div className="album">
      <div className="container">
        <form>
          <div className="mb-3">
            <label htmlFor="email" className="form-label">
              Email address
            </label>
            <input
              value={signUpData.email}
              onChange={onChangeSignUpData}
              type="email"
              className="form-control"
              id="email"
              name="email"
              aria-describedby="emailHelp"
            />
          </div>
          <div className="mb-3">
            <label htmlFor="password" className="form-label">
              Password
            </label>
            <input
              onChange={onChangeSignUpData}
              value={signUpData.password}
              type="password"
              className="form-control"
              id="password"
              name="password"
            />
          </div>
          <div className="mb-3">
            <label htmlFor="password" className="form-label">
              Password
            </label>
            <input
              onChange={onChangeSignUpData}
              value={signUpData.password}
              type="password"
              className="form-control"
              id="password"
              name="password"
            />
          </div>
          <div className="mb-3">
            <label htmlFor="password" className="form-label">
              Password
            </label>
            <input
              onChange={onChangeSignUpData}
              value={signUpData.password}
              type="password"
              className="form-control"
              id="password"
              name="password"
            />
          </div>

          <button
            onClick={() => {
              //해당 이벤트 핸들러 내의 axios 요청(to 서버)를 통해 받은 응답(response)값에 따라 응답 후 처리 로직 정의
              onClickSignUpButton()
                .then((res) => {
                  console.log(res);
                })
                .catch((err) => {
                  alert(err);
                });
            }}
            type="button"
            className="btn btn-primary"
          >
            로그인
          </button>
        </form>
      </div>
    </div>
  );
};

export default SignUpForm;
