import { useSelector } from "react-redux"; // store에서 특정 값을 지정하여 가져오는 모듈
import backUrl from "./../../data/port.json";
import { useEffect, useState } from "react";
import { Cookies, useCookies } from "react-cookie";
import axios from "axios";

const Update = () => {
  const [cookies, setCookie, removeCookie] = useCookies(["userData"]);

  const shortId = useSelector((state) => state.id.value); // store에서 state 요소 특정해서 가져오기
  const [updateData, setUpdateData] = useState({});

  useEffect(() => {
    console.log(shortId);
    findGetReviewData()
      .then((res) => {
        console.log(res);
        
      })
      .catch((e) => console.log(e));
  }, []);

  const findGetReviewData = async () => {
    return await axios.get(backUrl.url + `/posts/${shortId}/find`, {
      headers: {
        accessToken: cookies.userData.accessToken,
      },
    });
  };

  return (
    <div className="album">
      <div className="container">
        <h3 style={{}} className="mb-5">
          Update Page
        </h3>
        <div className="card" style={{ marginBottom: "10em" }}>
          <div className="card-img-top" style={{ textAlign: "center" }}>
            <img
              src="https://movie-phinf.pstatic.net/20201229_146/1609226288425JgdsP_JPEG/movie_image.jpg?type=m203_290_2"
              alt="..."
            />
          </div>
          <div className="card-body ">
            <h5 className="card-title mb-3">카드 제목</h5>
            <p className="card-text">카드 텍스트 예시 입니다.</p>
            <p className="card-text"></p>
            <input
              type="text"
              name="title"
              className="form-control"
              id="title"
              placeholder="사진 url을 입력해 주세요."
            />
          </div>
        </div>

        <div className="mb-3">
          <label htmlFor="title" className="form-label">
            Title
          </label>
          <input
            type="text"
            name="title"
            className="form-control"
            id="title"
            placeholder="제목을 입력해 주세요"
          />
        </div>
        <div className="mb-3">
          <label htmlFor="content" className="form-label">
            Content
          </label>
          <textarea
            className="form-control"
            id="content"
            rows="3"
            name="content"
            placeholder="내용을 입력해 주세요"
          ></textarea>
        </div>
        <div className="d-flex justify-content-end">
          <button
            type="button"
            className="btn btn-primary"
            style={{ marginRight: "1%" }}
          >
            submit
          </button>
          <button type="button" className="btn btn-danger">
            뒤로가기
          </button>
        </div>
      </div>
    </div>
  );
};

export default Update;
