import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";
import { useCookies } from "react-cookie";
import backUrl from "./../../data/port.json";

const MoreInfo = () => {
  const params = useParams();

  const [cookies, setCookie, removeCookie] = useCookies(["userData"]);
  const [moreInfoData, setMoreInfoData] = useState({});

  useEffect(() => {
    console.log(params); // params 값 확인
    findMoreInfoData()
      .then((res) => {
        setMoreInfoData(res.data);
      })
      .catch((e) => console.log(e));
  });

  const findMoreInfoData = async () => {
    return await axios.get(backUrl.url + `/posts/${params.id}/find`, {
      headers: {
        accessToken: cookies.userData.accessToken,
      },
    });
  };
  return (
    <div className="album">
      <div className="container">
        <h3 style={{}} className="mb-5">
          Detail Page
        </h3>
        <div
          className="card"
          style={{ marginBottom: "10em", textAlign: "center" }}
        >
          <div className="card-img-top">
            <img
              src={moreInfoData.title}
              alt="..."
            />
          </div>
          <div className="card-body">
            <h5 className="card-title mb-3">카드 제목</h5>
            <p className="card-text">카드 텍스트 예시 입니다.</p>
            <p className="card-text"></p>
            <input
              type="text"
              name="title"
              className="form-control"
              disabled
              id="title"
              placeholder={moreInfoData.img}
            />
          </div>
        </div>

        <div className="mb-3">
          <label htmlFor="title" className="form-label">
            Title
          </label>
          <div className="card">
            <p className="card-body">{moreInfoData.title}</p>
          </div>
        </div>
        <div className="mb-3">
          <label htmlFor="content" className="form-label">
            Content
          </label>
          <div className="card">
            <p className="card-body">{moreInfoData.content}</p>
          </div>
        </div>
        <div className="d-flex justify-content-end">
          <button type="button" className="btn btn-danger">
            뒤로가기
          </button>
        </div>
      </div>
    </div>
  );
};

export default MoreInfo;
