import { useSelector } from "react-redux"; // store에서 특정 값을 지정하여 가져오는 모듈
import backUrl from "./../../data/port.json";
import { useEffect, useState } from "react";
import { Cookies, useCookies } from "react-cookie";
import axios from "axios";

const Update = () => {
  
  // const shortId = useSelector((state) => state.id.value); // 리덕스 store에서 state 요소 특정해서 가져오기 // 새로고침하면 shortId는 비워지게됨

  const [shortId,setShortId] = useState('')
  const [cookies, setCookie, removeCookie] = useCookies(["userData"]);
  const [updateData, setUpdateData] = useState({});

  useEffect(() => {
    console.log(shortId);

    setShortId(useSelector(state => state.id.value));
    
    findGetReviewData()
      .then((res) => {
        console.log(res);
        setUpdateData(res.data);
      })
      .catch((e) => console.log(e));
  }, []);

  const findGetReviewData = async () => {
    return await axios.get(backUrl.url + `/posts/${shortId}/find`, {
      headers: {
        accessToken: cookies.userData.accessToken,
      },
    });
  };

  const onChangeUpdateData = (e) => {
    setUpdateData({
      ...updateData,
      [e.target.name]: e.target.value,
    });
  };

  return (
    <div className="album">
      <div className="container">
        <h3 style={{}} className="mb-5">
          Update Page
        </h3>
        <div className="card" style={{ marginBottom: "10em" }}>
          <div className="card-img-top" style={{ textAlign: "center" }}>
            <img src={updateData.img} alt="..." />
          </div>
          <div className="card-body ">
            <h5 className="card-title mb-3">카드 제목</h5>
            <p className="card-text">카드 텍스트 예시 입니다.</p>
            <p className="card-text"></p>
            <input
              defaultValue={""}
              value={updateData.img}
              disabled
              onChange={onChangeUpdateData}
              type="text"
              name="title"
              className="form-control"
              id="title"
              placeholder="사진 url을 입력해 주세요."
            />
          </div>
        </div>

        <div className="mb-3">
          <label htmlFor="title" className="form-label">
            Title
          </label>
          <input
            defaultValue={""}
            onChange={onChangeUpdateData}
            type="text"
            name="title"
            className="form-control"
            value={updateData.title}
            id="title"
            placeholder="제목을 입력해 주세요"
          />
        </div>
        <div className="mb-3">
          <label htmlFor="content" className="form-label">
            Content
          </label>
          <textarea
            defaultValue={""}
            onChange={onChangeUpdateData}
            className="form-control"
            value={updateData.content}
            id="content"
            rows="3"
            name="content"
            placeholder="내용을 입력해 주세요"
          ></textarea>
        </div>
        <div className="d-flex justify-content-end">
          <button
            type="button"
            className="btn btn-primary"
            style={{ marginRight: "1%" }}
          >
            수정
          </button>
          <button type="button" className="btn btn-danger">
            뒤로가기
          </button>
        </div>
      </div>
    </div>
  );
};

export default Update;
