import $ from "jquery";
import axios from "axios";

const SignInForm = (props) => {
  const { signInData, onChangeSignInData } = props;

  // DB와 요청/응답을 수행한다면 (무조건) 비동기 처리
  const onClickLoginButton = async () => {
    if (signInData.email === "") {
      alert("이메일을 입력해주세요");
      $("#email").focus();
      return; //onClickLoginButton 이벤트핸들러 자체를 나감
    }

    if (signInData.password === "") {
      alert("비밀번호를 입력해주세요");
      $("#password").focus();
      return;
    }
    // 요청할 url과 보내고싶은 데이터
    return await axios.post("http://localhost:8080/user/login", signInData); //  user/login에 해당하는 node.js 서버에 요청. signInData를 응답하는 서버단의 참조용 데이터로 전송.
  };
  return (
    <div className="album">
      <div className="container">
        <form>
          <div className="mb-3">
            <label htmlFor="email" className="form-label">
              Email address
            </label>
            <input
              value={signInData.email}
              onChange={onChangeSignInData}
              type="email"
              className="form-control"
              id="email"
              name="email"
              aria-describedby="emailHelp"
            />
          </div>
          <div className="mb-3">
            <label htmlFor="password" className="form-label">
              Password
            </label>
            <input
              onChange={onChangeSignInData}
              value={signInData.password}
              type="password"
              className="form-control"
              id="password"
              name="password"
            />
          </div>

          <button
            onClick={() => {
              onClickLoginButton
                .then((res) => {
                  console.log(res);
                })
                .catch((e) => {
                  console.log(e);
                });
            }}
            type="button"
            className="btn btn-primary"
          >
            로그인
          </button>
        </form>
      </div>
    </div>
  );
};

export default SignInForm;
